let fixedCols = [];

for (const key in $json) {
  const val = $json[key];

  if (!isNaN(val) && typeof val === "string") {
    $json[key] = parseFloat(val);
    fixedCols.push(key);
  }
}

return [
  {
    json: {
      ...$json,
      _types_fixed: fixedCols.length > 0,
      _fix_log: `Fixed types in: ${fixedCols.join(", ")}`
    }
  }
];

const mean = 50000;
const stdDev = 15000;
let isOutlier = false;

if ($json["Price"]) {
  const price = parseFloat($json["Price"]);
  const zScore = (price - mean) / stdDev;
  isOutlier = Math.abs(zScore) > 3;
}

return [
  {
    json: {
      ...$json,
      _is_outlier: isOutlier
    }
  }
];

return [
  {
    json: {
      result: "âœ… Row cleaned and passed all checks.",
      data: $json
    }
  }
];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

// Inputs (you can set from a previous node)
const chart_type = $json.chart_type || "bar";          // bar, line, pie
const x_field = $json.x_axis || "Region";
const y_field = $json.y_axis || "Sales";

// Prepare values
const rows = $input.all();
const grouped = {};
for (const row of rows) {
  const x = row.json[x_field] || "Unknown";
  const y = parseFloat(row.json[y_field]) || 0;
  grouped[x] = (grouped[x] || 0) + y;
}

const labels = Object.keys(grouped);
const data = labels.map(l => grouped[l]);

// Chart config
const config = {
  type: chart_type,
  data: {
    labels: labels,
    datasets: [{
      label: `${y_field} by ${x_field}`,
      data: data,
      backgroundColor: chart_type === "pie"
        ? ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF']
        : 'rgba(54, 162, 235, 0.7)',
      borderColor: 'rgba(54, 162, 235, 1)',
      borderWidth: 1
    }]
  },
  options: {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: `${chart_type.toUpperCase()}: ${y_field} by ${x_field}`,
        font: { size: 18 }
      },
      legend: {
        display: chart_type === "pie"
      }
    },
    scales: chart_type !== "pie" ? {
      y: {
        beginAtZero: true,
        title: { display: true, text: y_field }
      },
      x: {
        title: { display: true, text: x_field }
      }
    } : undefined
  }
};

const chart_url = `https://quickchart.io/chart?c=${encodeURIComponent(JSON.stringify(config))}`;

return [{
  json: {
    chart_url,
    chart_type,
    x_axis: x_field,
    y_axis: y_field
  }
}];

